import React, { useState } from 'react';
import Profile from './Profile';
import FeePayment from './FeePayment';
import Attendance from './Attendance';
import Results from './Results';
import Courses from './Courses';
import Placements from './Placements';
import ExamTimeTable from './ExamTimeTable';
import AdmitCard from './AdmitCard';
import Grievances from './Grievances';
import TimeTable from './TimeTable';

function Dashboard({ onLogout }) {
  const [activeTab, setActiveTab] = useState('home');

  const announcements = [
    {
      id: 1,
      title: "Techno Cultural Fest",
      content: "The annual Techno Cultural Fest 'Spectrum 2024' is in 2 weeks. Get ready for exciting events including coding competitions, robotics workshops, cultural performances, and more!",
      date: "2024-02-15"
    },
    {
      id: 2,
      title: "Mid-Semester Examinations",
      content: "Mid-semester examinations start in 12 days. Time table has been uploaded to the portal. Prepare well!",
      date: "2024-02-20"
    },
    {
      id: 3,
      title: "Workshop on AI & Machine Learning",
      content: "A two-day workshop on AI and Machine Learning will be conducted by industry experts from Google. Registration is mandatory. Limited seats available.",
      date: "2024-02-25"
    },
    {
      id: 4,
      title: "Sports Day Registration",
      content: "Annual Sports Meet 2024 registrations are now open. Register for track and field events, team sports, and indoor games through the sports portal.",
      date: "2024-03-01"
    },
    {
      id: 5,
      title: "Campus Placement Drive",
      content: "Major tech companies will be visiting for campus placements next month. Pre-placement talks start next week. Check eligibility criteria on the placement portal.",
      date: "2024-03-10"
    }
  ];

  const events = [
    { date: '2024-02-15', title: 'Techno Cultural Fest' },
    { date: '2024-02-20', title: 'Mid-Semester Examinations Start' },
    { date: '2024-02-25', title: 'AI & ML Workshop' },
    { date: '2024-03-01', title: 'Sports Day' },
    { date: '2024-03-10', title: 'Campus Placement Drive' },
    { date: '2024-03-15', title: 'End Semester Exams Start' },
    { date: '2024-03-30', title: 'End Semester Exams End' },
  ];

  const months = [
    "January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December"
  ];

  const currentDate = new Date();
  const [currentMonth, setCurrentMonth] = useState(currentDate.getMonth());
  const [currentYear, setCurrentYear] = useState(currentDate.getFullYear());

  const getFirstDayOfMonth = (month, year) => {
    return new Date(year, month, 1).getDay();
  };

  return (
    <div className="calendar-section">
      <div className="calendar-header flex justify-between items-center mb-4">
        <h2 className="text-xl font-bold">
          {months[currentMonth]} {currentYear}
        </h2>
        <div className="calendar-controls">
          <button
            className="px-2 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
            onClick={() => setCurrentMonth(currentMonth === 0 ? 11 : currentMonth - 1)}
          >
            &lt;
          </button>
          <button
            className="px-2 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
            onClick={() => setCurrentMonth(currentMonth === 11 ? 0 : currentMonth + 1)}
          >
            &gt;
          </button>
        </div>
      </div>
      <div className="calendar-grid grid grid-cols-7 gap-2">
        {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map((day, index) => (
          <div key={index} className="calendar-header-day text-center font-semibold">
            {day}
          </div>
        ))}
        {days}
      </div>
    </div>
  );
    return (
    <div className="flex h-screen bg-gray-100">
      <div className="w-64 bg-gray-800 text-white p-5 flex flex-col">
        <button className={`py-2 px-4 rounded-lg mb-2 ${activeTab === 'home' ? 'bg-blue-600' : 'hover:bg-blue-500'}`} onClick={() => setActiveTab('home')}>Home</button>
        <button className={`py-2 px-4 rounded-lg mb-2 ${activeTab === 'profile' ? 'bg-blue-600' : 'hover:bg-blue-500'}`} onClick={() => setActiveTab('profile')}>Profile</button>
        <button className={`py-2 px-4 rounded-lg mb-2 ${activeTab === 'attendance' ? 'bg-blue-600' : 'hover:bg-blue-500'}`} onClick={() => setActiveTab('attendance')}>Attendance</button>
        <button className={`py-2 px-4 rounded-lg mb-2 ${activeTab === 'results' ? 'bg-blue-600' : 'hover:bg-blue-500'}`} onClick={() => setActiveTab('results')}>Results</button>
        <button className={`py-2 px-4 rounded-lg mb-2 ${activeTab === 'timetable' ? 'bg-blue-600' : 'hover:bg-blue-500'}`} onClick={() => setActiveTab('timetable')}>Time Table</button>
        <button className={`py-2 px-4 rounded-lg mb-2 ${activeTab === 'calendar' ? 'bg-blue-600' : 'hover:bg-blue-500'}`} onClick={() => setActiveTab('calendar')}>Calendar of Events</button>
        <button className={`py-2 px-4 rounded-lg mb-2 ${activeTab === 'courses' ? 'bg-blue-600' : 'hover:bg-blue-500'}`} onClick={() => setActiveTab('courses')}>Courses</button>
        <button className={`py-2 px-4 rounded-lg mb-2 ${activeTab === 'examtimetable' ? 'bg-blue-600' : 'hover:bg-blue-500'}`} onClick={() => setActiveTab('examtimetable')}>Exam Time Table</button>
        <button className={`py-2 px-4 rounded-lg mb-2 ${activeTab === 'admitcard' ? 'bg-blue-600' : 'hover:bg-blue-500'}`} onClick={() => setActiveTab('admitcard')}>Admit Card</button>
        <button className={`py-2 px-4 rounded-lg mb-2 ${activeTab === 'placements' ? 'bg-blue-600' : 'hover:bg-blue-500'}`} onClick={() => setActiveTab('placements')}>Placements</button>
        <button className={`py-2 px-4 rounded-lg mb-2 ${activeTab === 'grievances' ? 'bg-blue-600' : 'hover:bg-blue-500'}`} onClick={() => setActiveTab('grievances')}>Grievances</button>
        <button className={`py-2 px-4 rounded-lg mb-2 ${activeTab === 'feepayment' ? 'bg-blue-600' : 'hover:bg-blue-500'}`} onClick={() => setActiveTab('feepayment')}>Fee Payment</button>
        <button className="mt-auto py-2 px-4 bg-gray-700 hover:bg-gray-600 rounded-lg" onClick={onLogout}>Logout</button>
      </div>

 <div className="flex-grow p-5 overflow-y-auto">
        {activeTab === 'home' && (
          <div className="bg-white p-5 rounded-lg shadow-md">
            <h2 className="text-xl font-bold">Welcome to the Dashboard</h2>
            <div className="mt-4">
              <h3 className="text-lg font-semibold">Announcements</h3>
              <ul className="list-disc pl-5">
                {announcements.map((announcement) => (
                  <li key={announcement.id} className="mt-1">{announcement.title}: {announcement.content}</li>
                ))}
              </ul>
            </div>
          </div>
        )}

        {activeTab === 'profile' && <Profile />}
        {activeTab === 'attendance' && <Attendance />}
        {activeTab === 'results' && <Results />}
        {activeTab === 'timetable' && <TimeTable />}
        {activeTab === 'calendar' && renderCalendar()}
        {activeTab === 'courses' && <Courses />}
        {activeTab === 'examtimetable' && <ExamTimeTable />}
        {activeTab === 'admitcard' && <AdmitCard />}
        {activeTab === 'placements' && <Placements />}
        {activeTab === 'grievances' && <Grievances />}
        {activeTab === 'feepayment' && <FeePayment />}
      </div>
    </div>
  );
}

export default Dashboard;
