// ApplyPlacement.js
import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Modal, Form, Button, Alert, ProgressBar } from 'react-bootstrap';
import { FaUpload, FaTimesCircle, FaCheckCircle } from 'react-icons/fa';

const ApplyPlacement = ({ company, onClose, studentData }) => {
  const [resume, setResume] = useState(null);
  const [answers, setAnswers] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitStatus, setSubmitStatus] = useState(null);
  const [validationErrors, setValidationErrors] = useState({});

  const questions = [
    {
      id: 1,
      question: "Why are you interested in this role?",
      placeholder: "Describe your motivation and interest in this position..."
    },
    {
      id: 2,
      question: "Describe a challenging project you've worked on.",
      placeholder: "Share details about the project, your role, and outcomes..."
    },
    {
      id: 3,
      question: "What are your key strengths relevant to this position?",
      placeholder: "List your relevant skills and experiences..."
    },
    {
      id: 4,
      question: "Where do you see yourself in 5 years?",
      placeholder: "Describe your career goals and aspirations..."
    }
  ];

  const handleResumeUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      if (file.type === 'application/pdf') {
        setResume(file);
        setValidationErrors({ ...validationErrors, resume: null });
      } else {
        setValidationErrors({ 
          ...validationErrors, 
          resume: 'Please upload a PDF file only' 
        });
      }
    }
  };

  const handleAnswerChange = (id, value) => {
    setAnswers(prev => ({ ...prev, [id]: value }));
    if (value.trim()) {
      setValidationErrors({ ...validationErrors, [id]: null });
    }
  };

  const validateForm = () => {
    const errors = {};
    if (!resume) errors.resume = 'Please upload your resume';

    questions.forEach(q => {
      if (!answers[q.id] || answers[q.id].trim().length < 50) {
        errors[q.id] = 'Please provide a detailed answer (minimum 50 characters)';
      }
    });

    setValidationErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!validateForm()) return;

    setIsSubmitting(true);
    setSubmitStatus('processing');

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));

      setSubmitStatus('success');
      setTimeout(() => {
        onClose();
      }, 2000);
    } catch (error) {
      setSubmitStatus('error');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <Modal show={true} onHide={onClose} size="lg" centered>
      <Modal.Header className="bg-primary text-white">
        <Modal.Title>
          Apply to {company.name} - {company.role}
        </Modal.Title>
        <Button variant="link" className="text-white" onClick={onClose}>
          <FaTimesCircle size={20} />
        </Button>
      </Modal.Header>

      <Modal.Body className="bg-light">
        <Form onSubmit={handleSubmit}>
          {/* Student Details Card */}
          <div className="bg-white p-3 rounded shadow-sm mb-4">
            <h5 className="border-bottom pb-2 mb-3">Student Details</h5>
            <div className="row">
              <div className="col-md-6">
                <p><strong>Name:</strong> {studentData.name}</p>
                <p><strong>Roll Number:</strong> {studentData.rollNumber}</p>
                <p><strong>Course:</strong> {studentData.course}</p>
              </div>
              <div className="col-md-6">
                <p><strong>CGPA:</strong> {studentData.cgpa}</p>
                <p><strong>Email:</strong> {studentData.email}</p>
              </div>
            </div>
          </div>

          {/* Resume Upload Section */}
          <div className="bg-white p-3 rounded shadow-sm mb-4">
            <Form.Group controlId="resume">
              <Form.Label className="fw-bold">Upload Resume (PDF only)</Form.Label>
              <div className="d-flex align-items-center">
                <Button 
                  variant="outline-primary" 
                  onClick={() => document.getElementById('resume-upload').click()}
                  className="me-3"
                >
                  <FaUpload className="me-2" />
                  Select Resume
                </Button>
                <span className="text-muted">
                  {resume ? resume.name : 'No file selected'}
                </span>
                <input
                  id="resume-upload"
                  type="file"
                  accept=".pdf"
                  onChange={handleResumeUpload}
                  style={{ display: 'none' }}
                />
              </div>
              {validationErrors.resume && (
                <Alert variant="danger" className="mt-2 py-1 px-2">
                  {validationErrors.resume}
                </Alert>
              )}
            </Form.Group>
          </div>

          {/* Application Questions */}
          <div className="bg-white p-3 rounded shadow-sm mb-4">
            <h5 className="border-bottom pb-2 mb-3">Application Questions</h5>
            {questions.map((q) => (
              <Form.Group key={q.id} className="mb-4">
                <Form.Label className="fw-bold">{q.question}</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={4}
                  placeholder={q.placeholder}
                  value={answers[q.id] || ''}
                  onChange={(e) => handleAnswerChange(q.id, e.target.value)}
                  isInvalid={!!validationErrors[q.id]}
                />
                <Form.Control.Feedback type="invalid">
                  {validationErrors[q.id]}
                </Form.Control.Feedback>
              </Form.Group>
            ))}
          </div>

          {/* Status Messages */}
          {submitStatus && (
            <Alert 
              variant={
                submitStatus === 'success' ? 'success' : 
                submitStatus === 'error' ? 'danger' : 
                'info'
              }
              className="d-flex align-items-center"
            >
              {submitStatus === 'processing' && (
                <>
                  <ProgressBar animated now={100} className="flex-grow-1" />
                  <span className="ms-3">Processing your application...</span>
                </>
              )}
              {submitStatus === 'success' && (
                <>
                  <FaCheckCircle className="me-2" />
                  Application submitted successfully!
                </>
              )}
              {submitStatus === 'error' && (
                <>
                  <FaTimesCircle className="me-2" />
                  Error submitting application. Please try again.
                </>
              )}
            </Alert>
          )}

          {/* Form Actions */}
          <div className="d-flex justify-content-end gap-2 mt-4">
            <Button 
              variant="secondary" 
              onClick={onClose}
              disabled={isSubmitting}
            >
              Cancel
            </Button>
            <Button 
              variant ="primary" 
              type="submit" 
              disabled={isSubmitting}
            >
              {isSubmitting ? 'Submitting...' : 'Submit Application'}
            </Button>
          </div>
        </Form>
      </Modal.Body>
    </Modal>
  );
};

export default ApplyPlacement;
